import { useAuthValue } from "../../context/AuthContext";
import { useFetchDocuments } from "../../hooks/useFetchDocuments";
import styles from "./MeusPets.module.css";

const Adocao = () => {
  const { user } = useAuthValue();
  const { documents: pets, loading, error } = useFetchDocuments("pets", user.uid);

  if (loading) return <p>Carregando pets...</p>;
  if (error) return <p>{error}</p>;
  if (pets.length === 0) return <p>Você ainda não cadastrou nenhum pet.</p>;

  return (
    <div className={styles.container}>
      <h2>Meus Pets</h2>
      <div className={styles.petList}>
        {pets.map((pet) => (
          <div key={pet.id} className={styles.petCard}>
            <img src={pet.image} alt={pet.nome} className={styles.petImage} />
            <h3>{pet.nome}</h3>
            <p><strong>Tipo:</strong> {pet.tipo}</p>
            <p><strong>Raça:</strong> {pet.raca}</p>
            <p><strong>Idade:</strong> {pet.idade}</p>
            <p><strong>Descrição:</strong> {pet.descricao}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Adocao;
