import { useEffect, useState } from "react";
import { useAuthentication } from "../../hooks/useAuthentication";
import styles from "./Register.module.css";
import logoOficial from "./img/logoPet.png";

const Register = () => {
  const [nome, setNome] = useState("");
  const [telefone, setTelefone] = useState("");
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [error, setError] = useState("");

  const { createUser, error: authError, loading } = useAuthentication();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    const user = {
      displayName: nome,
      email,
      password: senha,
    };

    await createUser(user);
  };

  useEffect(() => {
    if (authError) {
      setError(authError);
    }
  }, [authError]);

  return (
    <div className={styles.page}>
      <a href="/Home">
        <img src={logoOficial} alt="Logo" className={styles.logoOficial} />
      </a>

      <h2>Ficha cadastro do adotante</h2>

      <div className={styles.container}>
        <form id="formAdocao" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Nome Completo:"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
          />

          <input
            type="tel"
            placeholder="Telefone:"
            value={telefone}
            onChange={(e) => setTelefone(e.target.value)}
          />

          <input
            type="email"
            placeholder="E-mail:"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <input
            type="password"
            placeholder="Senha:"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
          />

          {!loading && (
            <button type="submit" id="btnEnviar" className={styles.btn}>
              ENVIAR
            </button>
          )}

          {loading && (
            <button className={styles.btn} disabled>
              Aguarde...
            </button>
          )}

          {error && <p className={styles.error}>{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default Register;
