import { useState } from "react";
import { useInsertDocument } from "../../hooks/useInsertDocument.js";
import { useNavigate } from "react-router-dom";
import { useAuthValue } from "../../context/AuthContext.js";

const CreatePost = () => {
  const [image, setImage] = useState("");
  const [nome, setNome] = useState("");
  const [tipo, setTipo] = useState("");
  const [raca, setRaca] = useState("");
  const [cor, setCor] = useState("");
  const [idade, setIdade] = useState("");
  const [sexo, setSexo] = useState("");
  const [porte, setPorte] = useState("");
  const [vacinas, setVacinas] = useState("");
  const [castrado, setCastrado] = useState("");
  const [descricao, setDescricao] = useState("");

  const { user } = useAuthValue();

  const navigate = useNavigate();

  const { insertDocument, response } = useInsertDocument("posts");

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormError("");

    try {
      new URL(image);
    } catch (error) {
      setFormError("A imagem precisa ser uma URL.");
    }

    const tagsArray = tags.split(",").map((tag) => tag.trim().toLowerCase());

    if (!title || !image || !tags || !body) {
      setFormError("Por favor, preencha todos os campos!");
    }

    console.log(tagsArray);

    console.log({
      image,
      nome,
      tipo,
      raca,
      cor,
      idade,
      sexo,
      porte,
      vacinas,
      castrado,
      descricao,
      uid: user.uid,
      createdBy: user.displayName,
    });

    if (formError) return;

    insertDocument({
      title,
      image,
      body,
      tagsArray,
      uid: user.uid,
      createdBy: user.displayName,
    });

    navigate("/");
  };

  return (
    <div className="container">
      <img src={logoPet} alt="Logo Pet" className="logo-oficial" />

      <h2 className="titulo">Ficha de Cadastro do Pet</h2>

      <form className="form" onSubmit={handleSubmit}>
        <input placeholder="Nome do pet" value={nome} onChange={(e) => setNome(e.target.value)} />
        <input placeholder="Tipo" value={tipo} onChange={(e) => setTipo(e.target.value)} />
        <input placeholder="Raça" value={raca} onChange={(e) => setRaca(e.target.value)} />
        <input placeholder="Cor" value={cor} onChange={(e) => setCor(e.target.value)} />
        <input placeholder="Idade" value={idade} onChange={(e) => setIdade(e.target.value)} />
        <input placeholder="Sexo" value={sexo} onChange={(e) => setSexo(e.target.value)} />
        <input placeholder="Porte" value={porte} onChange={(e) => setPorte(e.target.value)} />
        <input placeholder="Vacinas" value={vacinas} onChange={(e) => setVacinas(e.target.value)} />
        <input placeholder="Castrado" value={castrado} onChange={(e) => setCastrado(e.target.value)} />

        <textarea
          placeholder="Descrição do pet"
          maxLength={300}
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
        />

        <small>{descricao.length} / 300</small>

        <button type="submit">ENVIAR</button>
      </form>
    </div>
  );
};

export default CadastrarPet;
